import React, { useState } from 'react';
import './App.css';

interface Product {
  id: number;
  name: string;
  price: number;
  description: string;
  image: string;
  category: string;
}

const App: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([
    {
      id: 1,
      name: "Organic Cotton T-Shirt",
      price: 29.99,
      description: "Made from 100% organic cotton, this t-shirt is both comfortable and sustainable.",
      image: "https://example.com/tshirt.jpg",
      category: "clothing"
    },
    {
      id: 2,
      name: "Bamboo Toothbrush",
      price: 4.99,
      description: "Eco-friendly bamboo toothbrush with replaceable heads.",
      image: "https://example.com/toothbrush.jpg",
      category: "personal-care"
    }
  ]);

  const [cart, setCart] = useState<Product[]>([]);

  const addToCart = (product: Product) => {
    setCart([...cart, product]);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>GlobalGreen E-commerce</h1>
        <p>Sustainable Shopping Platform</p>
      </header>
      <main>
        <div className="products">
          {products.map((product) => (
            <div key={product.id} className="product-card">
              <img src={product.image} alt={product.name} />
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <p className="price">${product.price.toFixed(2)}</p>
              <button onClick={() => addToCart(product)}>Add to Cart</button>
            </div>
          ))}
        </div>
        <div className="cart">
          <h2>Shopping Cart</h2>
          {cart.map((item, index) => (
            <div key={index} className="cart-item">
              <p>{item.name}</p>
              <p>${item.price.toFixed(2)}</p>
            </div>
          ))}
          <p className="total">
            Total: ${cart.reduce((sum, item) => sum + item.price, 0).toFixed(2)}
          </p>
        </div>
      </main>
    </div>
  );
};

export default App;
