import React from 'react';

interface Product {
  id: number;
  name: string;
  price: number;
  description: string;
  image: string;
  category: string;
  organic: boolean;
  origin: string;
  stock?: number;
  isNew?: boolean;
}

interface ProductCardProps {
  product: Product;
  onAddToCart: (product: Product) => void;
}

const ProductCard: React.FC<ProductCardProps> = ({ product, onAddToCart }) => {
  return (
    <div className="product-card">
      <img src={product.image} alt={product.name} className="product-image" />
      <div className="product-info">
        <h3>{product.name}</h3>
        <p className="product-description">{product.description}</p>
        <div className="product-details">
          <span className="product-origin">Origin: {product.origin}</span>
          <span className={`product-organic badge ${product.organic ? 'badge-organic' : 'badge-conventional'}`}>{product.organic ? 'Organic' : 'Conventional'}</span>
          {product.isNew && <span className="badge badge-new">New</span>}
        </div>
        <p className="price">${product.price.toFixed(2)}</p>
        {product.stock === 0 ? (
          <button className="add-to-cart" disabled>Out of Stock</button>
        ) : (
          <button className="add-to-cart" onClick={() => onAddToCart(product)}>Add to Cart</button>
        )}
        <button className="view-details">View Details</button>
      </div>
    </div>
  );
};

export default ProductCard;
